import React from 'react'
import Card from '@material-ui/core/Card'
import CardActions from '@material-ui/core/CardActions'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import FALLBACK_IMAGE from './media/images/fallback_image.png';

const onMediaFallback = event => event.target.src = FALLBACK_IMAGE;
const imageFolder = "./media/images/";

const CardBlock = (props) => {
    console.log(props)
    return(
        <div>
            { props.contentArea ? (
                <Card >
                    <CardMedia style={{height: 200, paddingTop: '56.25%'}}
                    component="img"
                    image={imageFolder + props.contentArea.imageName + ".jpg"}
                    title={props.contentArea.ContentAreaName}
                    onError={onMediaFallback}
                    />
                    <CardContent>
                    <Typography gutterBottom variant="headline" component="h2">
                        {props.contentArea.ContentAreaName}
                    </Typography>
                    <Typography component="p">
                        {props.contentArea.Description}
                    </Typography>
                    </CardContent>
                    <CardActions>
                    <Button size="small" color="primary" href={props.contentArea.ContentAreaName.url} target="_blank">
                        Go To {props.contentArea.ContentAreaName}
                    </Button>
                    </CardActions>
                </Card>
            ) : null}
        </div>
    )
}
export default CardBlock
